export default ({ color, description, name, borderColor, fontColor, position }) => {
	const newPosition = position === 'right' ? `padding-right: 6px` : `padding-left: 6px`;
	return (
		<div class='pb-3'>
			<p className='py-2 text-white font-thin tracking-wide'>{description}</p>
			<a
				style={{ backgroundColor: color, border: `${borderColor} 2px solid`, color: `${fontColor}` }}
				className='w-full text-center py-3 rounded font-semibold'
				href='#'>
				{name}
			</a>
		</div>
	);
};
