export default ({ color, description, name, border }) => {
	return (
		<div>
			<p className='py-2 text-white font-thin tracking-wide'>{description}</p>
			<a
				style={{ backgroundColor: color, border: `${borderColor} 1px solid` }}
				className='block text-center py-3 rounded font-semibold'
				href='#'>
				{name}
			</a>
		</div>
	);
};
